import React from 'react';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormControl from '@material-ui/core/FormControl';
import FormLabel from '@material-ui/core/FormLabel';

let p = '';
let t = '';
let c = '';
let s ='';
let name = '';
export default function RadioButtonsGroup(props) {

name = props.name;

 function onSubmit(e){
   e.preventDefault();
   // Check if not empty on submission
   //could hash the password here
   if(t == ''){
       props.handleScreen(name,p,t,c,s);
   }
 }

  const handleP = (e) => {
    p = e.target.value
  };

  const handleT = (e) => {
    t = e.target.value
  };

  const handleC = (e) => {
    c = e.target.value
  };

  const handleS = (e) => {
    s = e.target.value
  };

  return (
    <form className={classes.form} noValidate onSubmit={onSubmit}>
    <FormControl component="fieldset">
      <FormLabel component="legend">Have you tested positive for COVID-19 in the last 30 days?</FormLabel>
      <RadioGroup aria-label="gender" name="gender1" value={value} onChange={handleP}>
        <FormControlLabel value="yes" control={<Radio />} label="Yes" />
        <FormControlLabel value="no" control={<Radio />} label="No" />
      </RadioGroup>
    </FormControl>
    <TextField
      variant="outlined"
      margin="normal"
      required
      fullWidth
      id="temperature"
      label="Temperature"
      name="temperature"
      type="text"
      onChange={handleT}
    />
    <FormControl component="fieldset">
      <FormLabel component="legend">Have you been in contact with someone who has recently been
      diagnosed with COVID-19, or has experienced multiple symptoms such as coughing,
      fever, shortness of breath, or loss of taste and/or smell?</FormLabel>
      <RadioGroup aria-label="Symptoms" name="Symptoms" value={value} onChange={handleC}>
        <FormControlLabel value="yes" control={<Radio />} label="Yes" />
        <FormControlLabel value="no" control={<Radio />} label="No" />
      </RadioGroup>
    </FormControl>
    <FormControl component="fieldset">
      <FormLabel component="legend">Have you experienced any symptoms of COVID-19, such as coughing, fever, shortness
       of breath, or loss of taste and/or smell?  Please select "No" if these symptoms stem
       from a preexisting condition.</FormLabel>
      <RadioGroup aria-label="gender" name="gender1" value={value} onChange={handleS}>
        <FormControlLabel value="yes" control={<Radio />} label="Yes" />
        <FormControlLabel value="no" control={<Radio />} label="No" />
      </RadioGroup>
    </FormControl>
    <Button
      type="submit"
      fullWidth
      variant="contained"
      color="primary"
      className={classes.submit}
    >
      Submit
    </Button>
    </form>
  );
}
